<script>
	import {userActivity,getuserDalance} from '@/api/user.js';
	import {toLogin} from '@/libs/login.js';
	import {mapGetters} from "vuex";
	import { alipayQueryPayResult } from '@/api/order.js';
	import recommend from '@/components/recommend/index';
	let app = getApp();
	export default {
		components: {
			recommend
		},
		data() {
			return {
				urlDomain: this.$Cache.get("imgHost"),
				hostProduct: [],
				isClose: false,
				activity: {},
				statistics:{},
				theme:app.globalData.theme,
				isNoCommodity: false // 是否显示缺省图
			};
		},
		computed: mapGetters(['isLogin', 'userInfo']),
		watch:{
			isLogin:{
				handler:function(newV,oldV){
					if(newV){
						this.get_activity();
						this.userDalance();
					}
				},
				deep:true
			}
		},
		onLoad() {
			if (this.isLogin) {
				// #ifdef H5
				var url = window.location.search;
				if(url){
					var theRequest = new Object();
					if (url.indexOf("?") != -1) {
					    var str = url.substr(1);
					    var strs = str.split("&");
					    for (var i = 0; i < strs.length; i++) {
							theRequest[strs[i].split('=')[0]] = decodeURI(strs[i].split('=')[1]);
					    }
					}
					this.orderId = theRequest.out_trade_no; //返回的订单号
					this.alipayQueryPay();
				}
				// #endif
				this.get_activity();
				this.userDalance();
			} else {
				toLogin();
			}
		},
		methods: {
			getRecommendLength(e) {
				this.isNoCommodity = e == 0 ? true : false;
			},
			/**
			 * 支付宝充值结果查询
			 */
			alipayQueryPay() {
				uni.showLoading({
					title: '查询中...'
				});
				alipayQueryPayResult(this.orderId).then(res => {
					this.userDalance();
					return this.$util.Tips({
						title: '充值成功'
					});
					uni.hideLoading();
				}).catch(err => {
					uni.hideLoading();
					return this.$util.Tips({
						title: err
					});
				})
			},
			onLoadFun: function() {
				this.get_activity();
				this.userDalance();
			},
			userDalance(){
				getuserDalance().then(res=>{
					this.statistics = res.data;
				})
			},
			// 授权关闭
			authColse: function(e) {
				this.isShowAuth = e
			},
			openSubscribe: function(page) {
				uni.navigateTo({
					url: page,
				});
			},
			/**
			 * 获取活动可参与否
			 */
			get_activity: function() {
				// let that = this;
				// userActivity().then(res => {
				// 	that.$set(that, "activity", res.data);
				// })
			}
		},
		onReachBottom() {
			this.$refs.recommendIndex.get_host_product();
		}
	}
</script>

